    constructor(service, winner) {
        this.service = service;
        this.winner = winner;
        this.winnerName = '';
        this.botChoice = Object.keys(Symbols);
        this.title = 'anularscissorsproj';
        this.scores = {
            computer: 0,
            person: 0,
